<style>



/* PRIMARY-BUTTON */

button,
.primary {
    font-size: 16px;
    color: #ffffff;
    font-weight: bold;
    font-family: var(--button);
    background-color: #1f425f;
    padding: 0.8rem 30px;
    display: inline-block;
    display: flex;
    gap: 1rem;
    justify-content: center;
    place-items: center;
    max-width: 200px;
    white-space: nowrap;
    animation-name: rotate_fish_out;
    animation-duration: 0.3s;
    animation-iteration-count: 1;
    animation-timing-function: linear;
    animation-fill-mode: both;
    border: #1f425f 1px solid;

}

button:hover,
.primary:hover {
    color: #1f425f;
    background-color: #ffffff;
    border: #1f425f 1px solid;
}




button,
.secondary {
    font-size: 16px;
    color: #1f425f;
    font-weight: bold;
    font-family: var(--button);
    background-color: #ffffff;
    padding: 0.8rem 30px;
    display: inline-block;
    display: flex;
    gap: 1rem;
    justify-content: center;
    place-items: center;
    max-width: 200px;
    white-space: nowrap;
    animation-name: rotate_fish_out;
    animation-duration: 0.3s;
    animation-iteration-count: 1;
    animation-timing-function: linear;
    animation-fill-mode: both;
}

button:hover,
.secondary:hover {
    color: #ffffff;
    background-color: #1f425f;
}

button:hover .button_icon path {
    fill: #1f425f;
}

button, .button_icon .secondary path {
    fill: red;
}

/* button:hover .secondary .button_icon path {
    fill: red;
} */










.button_icon {
    animation-name: rotate_fish_out;
    animation-duration: 0.3s;
    animation-iteration-count: 1;
    animation-timing-function: linear;
    animation-fill-mode: both;
}

button:hover .button_icon {
    animation-name: rotate_fish_in;
    animation-duration: 0.3s;
    animation-iteration-count: 1;
    animation-timing-function: linear;
    animation-fill-mode: both;
}



/* KEYFRAMES */
@keyframes rotate_fish_in {
    0% {
        transform: rotatey(0deg);
    }

    100% {
        transform: rotatey(180deg);
    }
}

@keyframes rotate_fish_out {
    0% {
        transform: rotatey(180deg);
    }

    100% {
        transform: rotatey(0deg);
    }
}


</style>